    </div>
</div>

<style>
    .sidebar-cashier {
        display: inline-flex;
        width: 250px;
        height: calc(100%);
        overflow-y: auto;
        padding: 20px;
        flex-direction: column;
        align-items: center;
        gap: 15px;
        position: relative;
        background: var(--White, #FFF);
        scrollbar-width: thin;
        scrollbar-color: #c1c1c1 #f5f5f5;
        transition: transform 0.3s ease, opacity 0.3s ease;
    }

    .align {
        display: flex;
        flex-direction: column;
        align-items: flex-start;
        gap: 20px;
        align-self: stretch;
        height: auto;
    }

    .pointer {
        cursor: pointer;
    }

    .sidebar-cashier::-webkit-scrollbar {
        width: 8px;
        border-radius: 10px;
    }

    .sidebar-cashier::-webkit-scrollbar-thumb {
        background-color: #c1c1c1;
        border-radius: 10px;
    }

    .sidebar-cashier::-webkit-scrollbar-track {
        background: #f5f5f5;
        border-radius: 10px;
    }

    .fee-2:hover {
        background: var(--Light-Blue, rgba(109, 207, 251, 0.20));
    }

    .payment:hover {
        background: var(--Light-Blue, rgba(109, 207, 251, 0.20));
    }

    .fee-deeper {
        max-height: 0;
        overflow: hidden;
        transition: max-height 0.3s ease;
    }

    .fee-deeper.open {
        max-height: 300px;
    }

    .fee {
        display: flex;
        height: 30px;
        align-items: center;
        justify-content: space-between;
        align-self: stretch;
        cursor: default;
    }

    .text {
        display: flex;
        height: 30px;
        align-items: center;
        gap: 20px;
    }

    .payment p {
        color: #000;
        font-family: Inter;
        font-size: 14px;
        font-style: normal;
        font-weight: 400;
        line-height: normal;
    }

    .fee-deeper{
        display: flex;
        flex-direction: column;
        align-items: flex-start;
        align-self: stretch;
        gap: 20px;
        transition: max-height 0.3s ease;
        height: auto;
        overflow-y: auto;
    }

    .fee h1 {
        color: var(--Silver, #AFAFAF);
        font-family: Inter;
        font-size: 14px;
        font-style: normal;
        font-weight: 400;
        line-height: normal;
    }

    .fee-2 {
        display: flex;
        justify-content: space-between;
        align-items: center;
        align-self: stretch;
    }

    .payment {
        display: flex;
        height: 30px;
        padding: 0px 0px 0px 45px;
        align-items: center;
        gap: 10px;
        align-self: stretch;
    }

    .fee-2:hover .svg-bottom path {
        stroke: #01B3EF;
    }

    #arrow-icon {
        transition: transform 0.3s ease;
    }
</style>

<script>
    function toggleDeeperFee() {
        const deeperElement = document.getElementById('fee-deeper');
        const arrowIcon = document.getElementById('arrow-icon');

        // Toggle lớp 'open' để bật/tắt hiệu ứng
        if (deeperElement.classList.contains('open')) {
            deeperElement.classList.remove('open');
            arrowIcon.style.transform = 'rotate(0deg)'; // Reset rotation
        } else {
            deeperElement.classList.add('open');
            arrowIcon.style.transform = 'rotate(180deg)'; // Rotate arrow
        }
    }
</script>
