        color: #01B3EF;
    }

    .general-deeper:hover, .score:hover, .conduct:hover, .result:hover, .tuition:hover, .fee-info:hover {
        background-color: rgba(108, 108, 108, 0.1);
        border-radius: 4px;
    }

    .general {
        display: flex;
        flex-direction: column;
        align-items: flex-start;
        justify-content: center;
        align-self: stretch;
        cursor: default;
