        display: flex;
        flex-direction: column;
        align-items: flex-start;
        align-self: stretch;
        height: 30px;
        padding: 5px 35px;
    }

    .study {
        display: flex;
        flex-direction: column;
        align-items: flex-start;
        justify-content: center;
        align-self: stretch;
        cursor: default;
    }

    .general h1, .fee h1, 
    .study h1 {
        color: var(--Silver, #AFAFAF);
        font-family: Inter;
        font-size: 16px;
        font-style: normal;
        font-weight: 400;
        line-height: normal;
    }

    .study-2 {
