        flex-direction: column;
        align-items: flex-start;
        align-self: stretch;
        gap: 20px;
    }

    .score, .conduct, .result, .tuition, .fee-info {
        display: flex;
        height: 30px;
        padding: 0px 35px;
        align-items: center;
        gap: 10px;
        align-self: stretch;
    }

    .fee {
        display: flex;
        flex-direction: column;
        align-items: flex-start;
        justify-content: center;
        align-self: stretch;
        cursor: default;
    }

    
    .fee-2 {
