<style>
    .sidebar-supervisor {
        display: flex;
        width: 250px;
        height: 964px;
        padding: 20px;
        flex-direction: column;
        align-items: center;
        gap: 20px;
        background: var(--White, #FFF);
    }

    .general, .study, .conduct-management {
        display: flex;
        flex-direction: column;
        align-items: flex-start;
        justify-content: center;
        align-self: stretch;
        cursor: default;
    }

    .general-2, .general-deeper, .statics, .student, .conduct, .study-2, .list, .reverse, .conduct-2, .category, .fault{
        cursor: pointer;
    }

    .general-2:hover,
    .statics:hover,
    .study-2:hover,
    .conduct-2:hover {
        background-color: rgba(108, 108, 108, 0.1);
        border-radius: 4px;      
    }

    .general-2:hover .text p,
    .statics:hover .text p,
    .study-2:hover .text p,
    .conduct-2:hover .text p {
        color: #01B3EF;
    }

    .general-deeper:hover, .student:hover, .conduct:hover, .list:hover, .reverse:hover, .category:hover, .fault:hover {
        background-color: rgba(108, 108, 108, 0.1);
        border-radius: 4px;
    }
    .general-2 {
        display: flex;
        height: 30px;
        justify-content: space-between;
        align-items: center;
        flex-shrink: 0;
        align-self: stretch;
    }

    .general-deeper{
        display: flex;
        flex-direction: column;
        align-items: flex-start;
        align-self: stretch;
        height: 30px;
        padding: 5px 30px;
    }

    .statics-deeper, .study-deeper, .fee-deeper {
        display: flex;
        flex-direction: column;
        align-items: flex-start;
        align-self: stretch;
        gap: 20px;
    }

    .general h1, .study h1, .fee h1, .conduct-management h1 {
        color: var(--Silver, #AFAFAF);
        font-family: Inter;
        font-size: 16px;
        font-style: normal;
        font-weight: 400;
        line-height: normal;
    }

    .statics, .study-2, .conduct-2 {
        display: flex;
        height: 30px;
        justify-content: space-between;
        align-items: center;
        flex-shrink: 0;
        align-self: stretch;
    }

    .text {
        display: flex;
        height: 30px;
        align-items: center;
        gap: 10px;
    }

    .text p, .general-deeper p, .statics-deeper p, .student p, .conduct p, .list p, .reverse p,
    .category p, .fault p {
        color: #000;
        font-family: Inter;
        font-size: 16px;
        font-style: normal;
        font-weight: 400;
        line-height: normal;
    }

    .student, .conduct, .list, .reverse, .category, .fault {
        display: flex;
        height: 30px;
        padding: 0px 35px;
        align-items: center;
        gap: 10px;
        align-self: stretch;
    }
</style>