<style>
    *,
    *::before,
    *::after {
        margin: 0;
        padding: 0 auto;
        box-sizing: border-box;
    }

    .director-heading {
        width: 100%;
        height: 60px;
        position: fixed;
        z-index: 1001;
        background-color: white;
    }

    .dashboard {
        display: flex;
        width: 100%;
        height: 100vh;
        flex-direction: column;
        align-items: flex-start;
        background: var(--White, #FFF);
        overflow: hidden;
    }

    .body {
        display: flex;
        align-items: flex-start;
        gap: 20px;
        margin-top: 60px;
        /* Đẩy nội dung xuống dưới heading */
        overflow: hidden;
        height: 100%;
        flex: 1;
        align-self: stretch;
        width: 100%;
        background: var(--light-grey, #F9FAFB);
    }

    .body-left {
        height: 100%;
        overflow-y: auto;
    }

    .right-dashboard {
        display: flex;
        padding: 20px;
        overflow-y: auto;
        flex-direction: column;
        align-items: flex-start;
        gap: 20px;
        flex: 1;
        align-self: stretch;
        color: #000;
        font-family: Inter;
        font-size: 16px;
        font-style: normal;
        font-weight: 400;
        line-height: normal;
        width: 100%;
        transform: translateX(0);
        transition: transform 0.3s ease;

    }

    .under {
        display: flex;
        align-items: flex-start;
        gap: 20px;
        flex: 1 0 0;
        align-self: stretch;
    }

    .under-left {
        display: flex;
        width: 70%;
        padding: 0px 10px;
        flex-direction: column;
        align-items: flex-start;
        gap: 20px;
        align-self: stretch;
    }

    .under-right {
        width: 30%;
        display: flex;
        padding: 20px;
        flex-direction: column;
        align-items: flex-start;
        gap: 20px;
        flex: 1 0 0;
        border-radius: 10px;
        background: var(--White, #FFF);
        color: #000;
        font-family: Inter;
        font-size: 16px;
        font-style: normal;
        font-weight: 700;
        line-height: normal;
    }

    .make-post {
        display: flex;
        padding: 20px;
        align-items: center;
        gap: 20px;
        align-self: stretch;
        border-radius: 10px;
        background: var(--White, #FFF);
    }

    .make-post img {
        width: 50px;
        height: 50px;
    }

    .making {
        display: flex;
        height: 40px;
        padding: 10px 20px;
        align-items: center;
        gap: 10px;
        flex: 1 0 0;
        border-radius: 20px;
        border: 1px solid rgba(0, 60, 60, 0.20);
    }

    .making a {
        text-decoration: none;
        color: var(--Silver, #AFAFAF);
        font-family: Inter;
        font-size: 16px;
        font-style: normal;
        font-weight: 400;
        line-height: normal;
    }

    .posts {
        display: flex;
        padding: 20px;
        flex-direction: column;
        align-items: flex-start;
        gap: 20px;
        flex: 1 0 0;
        align-self: stretch;
        border-radius: 10px;
        background: var(--White, #FFF);
        color: #000;
        font-family: Inter;
        font-size: 16px;
        font-style: normal;
        font-weight: 400;
        line-height: normal;
    }

    .posts img {
        width: 100%;
        height: 500px;
    }

    .post-heading {
        display: flex;
        width: 100%;
        justify-content: space-between;
        align-items: center;
    }

    .post-heading svg:hover {
        cursor: pointer;
    }

    .post-info {
        display: flex;
        align-items: center;
        gap: 20px;
    }

    .post-information {
        display: flex;
        width: 150px;
        gap: 20px;
        justify-content: space-between;
        align-items: center;
    }

    .post-information img {
        width: 50px;
        height: 50px;
        flex-shrink: 0;
    }

    .post-name {
        display: flex;
        width: 100%;
        padding: 10px 0px;
        flex-direction: column;
        justify-content: space-between;
        align-items: flex-start;
        flex-shrink: 0;
        align-self: stretch;
    }

    .post-name h1 {
        color: #000;
        font-family: Inter;
        font-size: 16px;
        font-style: normal;
        font-weight: 700;
        line-height: normal;
    }

    .post-name p {
        color: #000;
        font-family: Inter;
        font-size: 16px;
        font-style: normal;
        font-weight: 400;
        line-height: normal;
    }

    .post-date {
        display: flex;
        padding: 10px;
        align-items: flex-start;
        gap: 10px;
        border-radius: 10px;
        border: 1px solid var(--Malibu, #6DCFFB);
        background: var(--Light-Blue, rgba(109, 207, 251, 0.20));
        color: var(--Malibu, #6DCFFB);
        font-family: Inter;
        font-size: 16px;
        font-style: normal;
        font-weight: 400;
        line-height: normal;
    }

    .groups {
        display: flex;
        height: 40px;
        padding: 10px 20px;
        justify-content: space-between;
        align-items: center;
        align-self: stretch;
        color: #000;
        font-family: Inter;
        font-size: 16px;
        font-style: normal;
        font-weight: 400;
        line-height: normal;
    }

    .sidebar-director.active {
        left: 0;
    }
